var pdf = null;
var acroForm = null;

function getFieldArea(field) {
    fieldDict = field.getCOSObject();
    fieldAreaArray = fieldDict.getDictionaryObject(org.apache.pdfbox.cos.COSName.RECT);
    return new org.apache.pdfbox.pdmodel.common.PDRectangle(fieldAreaArray);
}


function handleImages(field, value, doc) {
    if( field != null ) {
        widgets = field.getWidgets();
        if (widgets != null && widgets.size() > 0) {
            annotationWidget = widgets.get(0); // just need one widget
            
            // Test if this is a File path and if it is load image from file...
            image = new java.io.File(value);
            if (image.exists()) {
            	log.warning("Reading image from file : " + image);
                pdImageXObject = org.apache.pdfbox.pdmodel.graphics.image.PDImageXObject.createFromFile(value, doc);
            }
            else {
            // As this is not a file, it can be a Base64, so read it as base64
            	log.warning("Reading image from base64 ...");
                pdImageXObject = org.apache.pdfbox.pdmodel.graphics.image.LosslessFactory.createFromImage(
                		pdf,
                		javax.imageio.ImageIO.read(
                				new java.io.ByteArrayInputStream(
                						javax.xml.bind.DatatypeConverter.parseBase64Binary(value)
                				)
                		)
                );
            }
            imageScaleRatio = pdImageXObject.getHeight() / pdImageXObject.getWidth();
            buttonPosition = getFieldArea(field);
            height = buttonPosition.getHeight();
            width = height / imageScaleRatio;
            x = buttonPosition.getLowerLeftX();
            y = buttonPosition.getLowerLeftY();
            
            log.warning("Image coords (x, y, w, h): " + x +","+ y +","+ width +","+ height +"," );

            pdAppearanceStream = new org.apache.pdfbox.pdmodel.interactive.annotation.PDAppearanceStream(pdf);
            pdAppearanceStream.setResources(new org.apache.pdfbox.pdmodel.PDResources());
            pdPageContentStream = new org.apache.pdfbox.pdmodel.PDPageContentStream(pdf, pdAppearanceStream);
            pdPageContentStream.drawImage(pdImageXObject, x, y, width, height);
            pdAppearanceStream.setBBox(new org.apache.pdfbox.pdmodel.common.PDRectangle(x, y, width, height));
            pdAppearanceDictionary = annotationWidget.getAppearance();
            if (pdAppearanceDictionary == null) {
                pdAppearanceDictionary = new PDAppearanceDictionary();
                annotationWidget.setAppearance(pdAppearanceDictionary);
            }
            pdAppearanceDictionary.setNormalAppearance(pdAppearanceStream);
            pdPageContentStream.close();
            
            log.warning("Image is inserted...");
        } else {
            log.warning("Misconfiguration of placeholder: '" + fieldName + "' - no widgets(actions) found");
        }
    }
}


function replaceField(name, data, doc){
	var field = acroForm.getField( name );
    if( field != null ) {
    	if(field.isReadOnly())
    		field.setReadOnly(false);
    		
    	switch (""+field.getClass().getSimpleName()) {
    		case "PDTextField":
    			field.setValue(data);
    			break;
    		case "PDCheckBox":
    			if (data == "true")
	    			field.check();
	    		else
	    			field.unCheck();
    			break;
    			
    		/* We handle pushButtons as a placeholder for images */
    		case "PDPushButton":
    			handleImages(field, data, doc);
    			break;
    			
    		default:
    			log.warning("UNKNOWN Field : " + field.getClass().getSimpleName());
    		break;
    	}
        field.setReadOnly(true);
    }
    else {
        log.warning( "No field found with name:" + name );
    }
}

