var pdf = null;
var acroForm = null;

function listChlidrenFields(field){
}

function addFieldInfos(node, name){
	var el = context.addTextNode(node, "FIELD", name);
	var field = acroForm.getField( name );
    if( field != null ) {
    	el.setAttribute("readOnly", field.isReadOnly()+"");
    	el.setAttribute("type", field.getClass().getSimpleName()+"");
    	if(field.getClass().getSimpleName() == "PDNonTerminalField"){
    		var childrenFields = field.getChildren().toArray();
    		for ( var i=0; i<childrenFields.length; i++ ) {
				addFieldInfos(el, childrenFields[i].getFullyQualifiedName());
			}
    	}
    }
    else {
        log.warning( "No field found with name:" + name );
    }
}

try {
	// Load PDF file and Form Catalog:
	var file = new java.io.File(tplPDF);
	pdf = org.apache.pdfbox.pdmodel.PDDocument.load(file);
	var docCatalog = pdf.getDocumentCatalog();
	acroForm = docCatalog.getAcroForm();
	var fields = acroForm.getFields().toArray();
	var nodeFields = context.addTextNodeUnderRoot("FIELDS", "");
	for ( var i=0; i<fields.length; i++ ) {
		addFieldInfos(nodeFields, fields[i].getFullyQualifiedName());
	}
} catch(e){
	log.warning("ERROR: " + e);
}
